:root {
  --terminal-bg: #202020;
}

* {
  box-sizing: border-box;
}

body {
  background: rgb(30, 30, 30);
  color: rgb(60,60,60);
  font-family: Arial, Helvetica, sans-serif;
  margin: 0;
  padding: 0;
  position: relative;
}

a {
  color: rgb(57, 211, 250);
  text-decoration: none;
}

#desktop {
  align-items: center;
  background: rgb(30, 30, 30);
  background: url(8f2416309697403db4658eb16bfdc4a7.jpeg) 50% 50% / cover no-repeat;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  height: 100vh;
  overflow: hidden;
  position: relative;
  touch-action: none;
  user-select: none;
}

.last-clicked {
  z-index: 1000;
}

.panel {
  position: absolute;
  border-radius: 0.5rem;
  box-shadow: 0.1rem 0.2rem 2.1rem rgba(0,0,0,0.4);
  display: flex;
  flex-direction: column;
}

.panel-header {
  background: #f8f8f8;
  background: linear-gradient(#f5f5f5, #c5c5c5);
  border-top-left-radius: 0.5rem;
  border-top-right-radius: 0.5rem;
  display: flex;
  flex: 0 0 auto;
  padding: 0.4rem;
}

.panel.full-screen {
  border-radius: 0 !important;
  top: 0 !important;
  bottom: 0 !important;
  right: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
}

.panel.full-screen .panel-header {
  border-radius: 0 !important;
}

#terminal {
  background: var(--terminal-bg);
  color: white;
  display: flex;
  flex-direction: column;
  font-family: "Courier New", "Courier";
  height: 20.5rem;
  width: 33rem;
  touch-action: none;
  user-select: none;
  left: 5%;
  top: 10%;
  word-break: break-all;
  z-index: 2;
}

#terminal .command {
  font-size: 1rem;
  opacity: 0.6;
}

p.command::before {
  content: '$';
  margin-right: 0.6rem;
}

.result {
  margin-bottom: 1.4rem;
}

#my-contact, #terminal #my-description, #my-name {
  font-size: 1.7rem;
}

#terminal-body {
  flex: 1;
  padding: 1rem;
}

p {
  margin: 0;
}

p.active::after {
  content: '|';
  opacity: 1;
  font-weight: 100;
  position: relative;
  top: -0.07rem;
  left: -0.2rem;
  -webkit-animation: blink 0.7s infinite;
  -moz-animation: blink 0.7s infinite;
  -ms-animation: blink 0.7s infinite;
  -o-animation: blink 0.7s infinite;
  animation: blink 0.7s infinite;
}

@keyframes blink {
  0% { opacity: 1; }
  50% { opacity: 0; }
  100% { opacity: 1; }
}

#ls, #my-name, #my-contact, #blank, #cat, #my-description {
  display: none;
}

#my-name {
  line-height: 0.9;
}

.buttons {
  align-items: center;
  flex-direction: row;
  display: flex;
  margin-right: 0.5rem;
  position: relative;
}

.panel-button {
  border-radius: 50%;
  line-height: 0;
  margin-right: 0.4rem;
  height: 0.75rem;
  width: 0.75rem;
}

.panel-button.red {
  background: #f96256;
  border: 1px solid #f65549;
}

.panel-button::after {
  color: rgba(0,0,0,0.5);
  font-family: Arial, Helvetica, sans-serif;
  font-size: 0.7rem;
  position: relative;
  visibility: hidden;
  top: 4.8px;
  left: 1.8px;
}

.panel-button.red::after {
  content: '\00d7';
}

.panel-button.yellow::after {
  content: '\2013';
  top: 4px;
}

.panel-button.green::after {
  content: '+';
}

#chrome {
  background: #fff;
  display: none;
  overflow: hidden;
  top: 3%;
  height: 35rem;
  width: 50rem;
}

#chrome .panel-button::after {
  top: 4px;
  left: 2px;
}

#chrome .panel-button.yellow::after {
  top: 3px;
  left: 2.1px;
}

.buttons:hover .panel-button::after {
  visibility: visible;
}

#url-wrapper {
  align-items: center;
  border-bottom: 1px solid rgba(0,0,0,0.2);
  display: flex;
  flex: 0 0 auto;
  padding: 0.25rem 0.5rem;
  width: 100%;
  height: 35px;
}

#url-arrows {
  align-items: center;
  display: flex;
}

#url-arrows img {
  margin-right: 0.7rem;
  width: 1.2rem;
}

#url {
  align-items: center;
  background: rgba(0,0,0,0.05);
  border-radius: 1rem;
  display: flex;
  padding: 0.2rem 1.5rem;
  height: 100%;
  width: 100%;
}

#back-arrow {
  transform: rotate(135deg);
}

#forward-arrow {
  transform: rotate(-45deg);
}

#url-arrows #refresh {
  width: 0.9rem;
}

.panel-button.yellow {
  background: #fdbc3d;
  border: 1px solid #ffb524;
}

.panel-button.green {
  background: #33c948;
  border: 1px solid #2dbb41;
}

.panel-body {
  display: flex;
}

#chrome .panel-body {
  flex-direction: column;
  justify-content: center;
  overflow: scroll;
}

#projects {
  overflow: auto;
  padding: 3rem;
}

.project-card {
  align-items: center;
  box-shadow: 0.1rem 0.3rem 1rem rgba(0,0,0,0.25);
  border-radius: 0.3rem;
  display: flex;
  margin: 0 auto 2.5rem;
  padding: 1.8rem 1rem;
  height: 16rem;
  width: 40rem;
}

.project-card h3 {
  margin: 0 0 0.3rem 0;
}

.project-card a {
  color: #0096cc;
  display: inline-block;
  font-size: 0.85rem;
  margin-bottom: 2rem;
}

.project-img {
  flex: 1 1 40%;
  text-align: center;
}

.project-img img {
  width: 12rem;
}

#abcmouse-img {
  width: 8.5rem;
}

#aa-icon-img {
  width: 9rem;
}

#aofljs-img {
  width: 10rem;
}

.project-details {
  flex: 1 1 60%;
  padding: 0 1.5rem;
}

.project-details p {
  font-size: 0.9rem;
  line-height: 1.5;
  text-align: left;
}

.project-details a:hover {
  color: #91b3bf;
}

#dock {
  align-items: center;
  background-color: rgba(235,235,235,0.7);
  padding: 0.5rem 0 0.5rem 0.8rem;
  border: 1px solid rgba(0,0,0,0.1);
  border-radius: 0.5rem 0.5rem 0 0;
  box-shadow: 0 0.5rem 1rem rgba(0,0,0,0.25);
  border-top-left-radius: 0.5rem;
  border-top-right-radius: 0.5rem;
  display: flex;
  margin-bottom: 0;
  min-height: 5.87rem;
  min-width: 21.087rem;
  position: fixed;
  bottom: 0;
  z-index: 2000;
  transition: transform .1s ease-in-out;
}

#dock.collapsed {
  transform: translateY(100%);
}

#dock > div {
  position: relative;
}

.dock-title {
  border-radius: 0.3rem;
  background-color: rgba(215,215,215,1);
  box-shadow: 0 0.5rem 1rem rgba(0,0,0,0.25);
  padding: 0.25rem 0.9rem;
  position: absolute;
  top: -3.4rem;
  visibility: hidden;
}

.dock-title::after {
  content: '';
  position: absolute;
  top: 100%;
  left: 50%;
  margin-left: -0.4rem;
  width: 0;
  height: 0;
  border-top: solid 0.4rem rgba(215,215,215,1);
  border-left: solid 0.4rem transparent;
  border-right: solid 0.4rem transparent;
}

#dock > div:hover .dock-title {
  visibility: visible;
}

#dock div:first-of-type .dock-title {
  right: 0.2rem;
}

#dock div:nth-of-type(2) .dock-title {
  top: -3.15rem;
  right: -0.1rem;
}

#dock div:nth-of-type(3) .dock-title {
  top: -3.3rem;
  right: -0.1rem;
}

#dock div:last-of-type .dock-title {
  top: -3.15rem;
}

#dock img {
  max-width: 4.5rem;
}

#dock img:hover {
  cursor: pointer;
}

#dock div:not(#work) img {
  margin-right: 0.4rem;
}

#contact {
  margin-right: 0.8rem;
}

#work img {
  margin-right: 0.5rem;
  width: 4.5rem;
}

#about img {
  width: 4rem;
}

#contact img {
  width: 4.2rem;
}

#trash {
  border-left: 1px solid #868686;
  padding-left: 0.4rem;
}

@media only screen and (max-width: 568px)  {
  .hide-mobile {
    display: none !important;
  }

  #desktop {
    background: url(da71b233fe4cbc47e9193148b4ef18fb.jpg) 50% 50% / cover no-repeat;
  }

  #dock {
    background-color: rgba(235,235,235,0.5);
    bottom: 1rem;
    border-radius: 1rem;
    justify-content: center;
    min-width: auto;
    width: 90%;
  }
}
